```C++
public:
bool insert(const K& key, const V& value)
{
    if (_root == nullptr)//Á©∫Ê†ë
    {
        _root = new Node(key, value);
        return true;
    }

    Node* cur = _root;
    Node* parent = nullptr;
    while (cur)
    {
        if (key < cur->_key)//keyÊØîÂΩìÂâçËäÇÁÇπÂ∞èÔºåÂæÄÂ∑¶Â≠êÊ†ëËµ∞
        {
            parent = cur;
            cur = cur->_left;
        }
        else if (key > cur->_key)//ÂæÄÂè≥Â≠êÊ†ëËµ∞
        {
            parent = cur;
            cur = cur->_right;
        }
        else
        {
            return false;//keyÂ∑≤Â≠òÂú®ÔºåÊèíÂÖ•Â§±Ë¥•
        }
    }
    //Ê≠§Êó∂cur‰∏∫nullptr, parent‰∏∫curÁöÑÁªìÁÇπ
    if (key < parent->_key)parent->_left = new Node(key, value);
    else parent->_right = new Node(key, value);
    return true;//ÊàêÂäüÊèíÂÖ•
}
```

