#include <vector>
using namespace std;

template<class T>
void adjust_up(vector<T>& arr, int child)
{
	int parent = (child - 1) / 2;
	while (parent != child)//parent == child == 0Êó∂
	{
		if (arr[parent] < arr[child])//‰∏çÊª°Ë∂≥Â§ßÂ†Ü
		{
			swap(arr[parent], arr[child]);//‰∫§Êç¢
			child = parent;//ÁªßÁª≠Âêë‰∏äË∞ÉÊï¥,Ëø≠‰ª£childÂíåparent
			parent = (child - 1) / 2;
		}
		else//ÂÆåÊàêÂª∫Â†ÜÔºåÈÄÄÂá∫Âæ™ÁéØ
		{
			break;
		}
	}
}

template<class T>
void adjust_down(vector<T>& arr,int sz , int parent)
{
	int child = parent * 2 + 1;
	if (child + 1 < sz && arr[child + 1] > arr[child]) child++;//ÂèñËæÉÂ§ßÁöÑÂ≠©Â≠ê

	while (child < sz)
	{
		if (arr[parent] < arr[child])
		{
			swap(arr[parent], arr[child]);
			parent = child;
			int child = parent * 2 + 1;
			if (child >= sz) break;
			if (child + 1 < sz && arr[child + 1] > arr[child]) child++;
		}
		else
		{
			break;
		}
	}
}

template <class T>
void heap_sort(vector<T>& arr)
{
	int sz = arr.size();

