	}

	for (int i = sz-1; i >= 1; i--)
	{
		swap(arr[0], arr[i]);//ÈÄâÂá∫ÊúÄÂ§ßÁöÑÂÖÉÁ¥†ÊîæÂú®Êú´Â∞æ
		adjust_down(arr,i,0);//Âêë‰∏ãË∞ÉÊï¥Âª∫Â†Ü
	}
}
