    _MergeSort(arr,left,mid,tmp);//ÊéíÂ∫èÂ∑¶ÂçäËæπ
    _MergeSort(arr,mid+1,right,tmp);//ÊéíÂ∫èÂè≥ÂçäËæπ

    //ÂêàÂπ∂Êï∞ÁªÑ
    	int cur1 = left;
	int cur2 = mid + 1;
	int cur = left;

	while (cur1 <= mid && cur2 <= right)
	{
		if (arr[cur1] < arr[cur2])
		{
			tmp[cur++] = arr[cur1++];
		}
		else
		{
			tmp[cur++] = arr[cur2++];
		}
	}

	while (cur1 <= left)
		tmp[cur++] = arr[cur1++];
	while (cur2 <= right)
		tmp[cur++] = arr[cur2++];

	for (int i = left; i <= right; i++)
		arr[i] = tmp[i];
}

void MergeSort(vector<int>& arr)
{
    vector<int> tmp(arr.size());//Áî®tmpÂºÄËæüÈ¢ùÂ§ñÁ©∫Èó¥Áî®‰∫éÂêàÂπ∂Êï∞ÁªÑ
    _MergeSort(arr,0,arr.size()-1);
}
