int CountMine(char board[ROWS][COLS], int x, int y)
{
	int sum = 0;
	for (int i = x - 1; i <= x + 1; i++)//‰∏ä‰∏≠‰∏ã‰∏âË°å
	{
		for (int j = y - 1; j <= y + 1; j++)//Â∑¶‰∏≠Âè≥‰∏âÂàó
		{
			if (i != x || j != y)
			{
				if (board[i][j] == Bomb)
				{
					sum++;
				}

			}
		}
	}
	return sum;
}

void SetNum(char board[ROWS][COLS])
{
	//ÈÅçÂéÜ‰∫åÁª¥Êï∞ÁªÑ
	for (int i = 1; i <= ROW; i++)
	{
		for (int j = 0; j <= COLS; j++)
		{
			if (board[i][j] == Blank)//‰ªÖÊìç‰ΩúÈùûÈõ∑ÁöÑÊ†ºÂ≠ê
			{
				if (CountMine(board, i, j))
				{
					board[i][j] = '0' + CountMine(board, i, j);//Â∞ÜËøîÂõûÁöÑÊï∞Â≠óËΩ¨ÂåñÊàêÂ≠óÁ¨¶
				}
			}
		}
	}
}

