int CountMine(char board[ROWS][COLS], int x, int y)
{
	int sum = 0;
	for (int i = x - 1; i <= x + 1; i++)//‰∏ä‰∏≠‰∏ã‰∏âË°å
	{
		for (int j = y - 1; j <= y + 1; j++)//Â∑¶‰∏≠Âè≥‰∏âÂàó
