void ExternalCommand()
{
    pid_t id = fork();
    if(id <0)
    {
        perror("fork");
        return;
    }
    else if(id == 0)//child
    {
        execvp(argv[0],argv+1);//+1‰πãÂêéÊâçÊòØÂèÇÊï∞ÂàóË°®
        exit(EXIT_CODE);
    }
    else
    {
        int status = 0;
